package ch04;

public class ifExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int score = 70;
		if (score >= 90) {
			if (score >= 97 ) {
				System.out.println("A+");
			} else if (score >= 90 ) {
				System.out.println("A");
			} else {
				System.out.println("A-");
			}
			if (score >= 87 ) {
				System.out.println("B+");
			} else if (score >= 80 ) {
				System.out.println("B");
			} else {
				System.out.println("B-");
			}
			if (score >= 77 ) {
				System.out.println("C+");
			} else if (score >= 70 ) {
				System.out.println("C");
			} else {
				System.out.println("C-");
			}
			if (score >= 67 ) {
				System.out.println("D+");
			} else if (score >= 60 ) {
				System.out.println("D");
			} else {
				System.out.println("-");
			}
			if (score <= 50 ) {
				System.out.println("F");
			}
		}
		
		
		/*
		 * int price = 1000; String grade = "normal"; // int discount =
		 * 
		 * // if () { // System.out.println(price * 1.5); }
		 */
		
		
		
		
	}

}
